INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('1', '8', 'Woody, el juguete favorito de Andy, se siente amenazado por la inesperada llegada de Buzz Lightyear, el guardián del espacio.', 'Woody', 'https://static.wikia.nocookie.net/kingdomhearts/images/2/2e/Woody_KHIII.png/revision/latest?cb=20190326204307&path-prefix=es', '0.5');
INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('2', '6', 'Es un superhéroe de juguete y una figura de acción en la franquicia.', 'Buzz Lightyear', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR-wyQx0hn7zlgfw1V6qrgjV93seHowmpT9Pw&usqp=CAU', '0.5');
INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('3', '10', 'Es el dueño de los juguetes y mejor amigo de Woody y Buzz', 'Andy', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTIINrjMF0_qPAZEma7x4TFG2z7aAcr2Y6KKw&usqp=CAU', '20');
INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('4', '8', 'Nemo es el hijo de Marilin y esta perdido por el oceano', 'Nemo', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQhToG80qiCA3e-Xk7LvY8HhxSfyQJRv38X0g&usqp=CAU', '1.2');
INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('5', '20', 'Marlin es un pez payaso, padre de Nemo que esta perdido y junto a Dory atraviesan el oceano para buscarlo', 'Marlin', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR-lXNoSWfjn9Q-Jg8mvamfHsiL92YHge4_EQ&usqp=CAU', '1.7');
INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('6', '20', 'Dory no tiene buena memoria, pero se hace mejor amiga de Marlin y cruzan el oceano juntos para buscar a Nemo', 'Dory', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRiphPmmYIu0Oy8OKT0Bv-yXYRmmBMN_Nenfg&usqp=CAU', '1.2');
INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('7', '30', 'Po es un panda y se convierte en el guerrero dragon', 'Po', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQe-NcQ0fGkc7xbsIxXPMsiIkteccek0YyJJQ&usqp=CAU', '102');
INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('8', '72', 'Él fue estudiante del Gran Maestro Oogway, y es el maestro de los guerreros más grandes de China, incluyendo a Po, los Cinco Furiosos y Tai Lung.', 'Shifu', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSt2WY1iZhHZBCKVfvab6yaEE95UGKVaLZv4Q&usqp=CAU', '20');
INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('9', '39', 'un multimillonario magnate empresarial y filántropo estadounidense, ingenioso científico, que sufrió una grave lesión en el pecho durante un secuestro en el Medio Oriente.', 'Iron Man', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR-kozxwc7GxlZTo_YIHor-W0ghh_d-_DMFpQ&usqp=CAU', '70');
INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('10', '10540', 'Thor está buscando la paz interior, pero la irrupción de Gorr, el asesino de dioses, le obliga a volver al combate.', 'Thor', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRY0O4l76eP08AXxsOwJuFQjyubTuGxroFgmw&usqp=CAU', '77');
INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('11', '10540', 'Thanos es un supervillano ficticio que aparece en los cómics estadounidenses publicados por Marvel Comics', 'Thanos', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTQx1GM8yog5jZxct88IU17N0BIfYnikPwx4g&usqp=CAU', '550');
INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('12', '41', 'un superhéroe que aparece en los cómics estadounidenses publicados por la editorial Marvel Comics, es considerado el personaje más fuerte de Marvel Comics.', 'Hulk', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTuGVFnJMDIrVcpaum4R8C-z24-UkowiWpL0g&usqp=CAU', '550');
INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('13', '41', 'Trabaja en una empresa, aunque anteriormente llevaba una doble vida secreta y era conocido como Mr. Increíble, un superhéroe con gran fuerza.', 'Bob', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRPfZ75A_Unm-M6SLuEYRMFjNj--NpICPSUOA&usqp=CAU', '70');
INSERT INTO characters (id_character, age, history, name, url_image, weight) VALUES ('14', '10', 'Violeta, la hija mayor de Bob y Helen Parr, nace con la capacidad sobrehumana de volverse invisible y generar campos de fuerza', 'Violeta', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQUzrBcMfu7a5l_GdTJLaIigkCjkePBnodUVQ&usqp=CAU', '21');
